import React from "react";
import "./login.css";

function LoginPage() {
  const signUp = document.getElementById("sign_up");
  const logInUrl = "http://localhost:8080/login";
  const onSuccessUrl = "http://localhost:3000/";

  const urlParams = new URLSearchParams(window.location.search);
  if (urlParams.has("error")) {
    document
      .querySelector("#login_form_box")
      .insertAdjacentHTML(
        "afterbegin",
        `<div id='error_div'>${urlParams.get("error")}</div>`
      );
    setTimeout(function () {
      document.querySelector("#error_div").remove();
    }, 3000);
  }

  if (urlParams.has("success")) {
    document
      .querySelector("#login_form_box")
      .insertAdjacentHTML(
        "afterbegin",
        `<div id='success_div'>${urlParams.get("success")}</div>`
      );
    setTimeout(function () {
      document.querySelector("#success_div").remove();
    }, 3000);
  }

  return (
    <div className="div_login">
      <div id="login_box">
        <div className="login_box_logo">
          <img
            src={process.env.PUBLIC_URL + "/images/ulm_logo.png"}
            alt="ULM logo"
            className="ulm_logo"
          />
        </div>
        <div className="login_form">
          <form action="" id="login_form_box">
            <label for="warhawks_email"> Warhawks Email</label>
            <br />
            <input type="email" id="warhawks_email" name="w_email" />

            <label for="password" className="password_label">
              {" "}
              Password
            </label>
            <br />
            <input type="password" id="warhawks_password" name="w_password" />

            <button type="submit" id="submit_form">
              {" "}
              Continue{" "}
            </button>
          </form>
        </div>
        <div className="form_sign">
          <div className="form_sign_text">
            {" "}
            <span id="forgot_password" onClick="goToForgotPassWord()">
              Forgot Password{" "}
            </span>
          </div>
          <div id="new_account">
            Need a new account ?
            <a href="/signup">
              <span id="signup_ref"> Sign Up</span>
            </a>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LoginPage;
